    <script>
         $(function() {
            const sanctumToken = localStorage.getItem('sanctum_token');

            if (sanctumToken) {
                // if sanctum token is available
                $('#authenticated-section').show();
                $('#unauthenticated-section').hide();
            }else{
                // if sanctum token is available
                $('#authenticated-section').hide();
                $('#unauthenticated-section').show();
            }
        });
    </script>
